-- -------------
-- 设置客户端连接服务器的编码为utf-8
-- -------------
SET NAMES UTF8;
-- -------------
-- 丢弃数据库，如果存在
-- -------------
DROP DATABASE IF EXISTS co;
-- -------------
-- 创建数据库，设置存储的编码
-- -------------
CREATE DATABASE co CHARSET=UTF8;
-- -------------
-- 进入数据库
-- -------------
USE co;
-- -------------
-- 创建用户列表
-- -------------
DROP TABLE IF EXISTS co_user;
CREATE TABLE co_user(
	uid INT PRIMARY KEY,
	uname VARCHAR(32),
	upwd  VARCHAR(32),
	avatar VARCHAR(128)
);
-- -------------
-- 创建购物车列表
-- -------------
DROP TABLE IF EXISTS co_cart;
CREATE TABLE co_cart(
	table_id INT PRIMARY KEY,
	menu_id INT not null,
	count INT not null,
	soup_id INT not null
);
-- -------------
-- 创建桌号表
-- -------------
DROP TABLE IF EXISTS co_assess;
CREATE TABLE co_assess(
	t_id INT PRIMARY KEY,
	t_name VARCHAR(32) not null,
	soup_id int ,
	soup_name VARCHAR(32)
);
-- -------------
-- 创建点餐单表
-- -------------
DROP TABLE IF EXISTS co_order;
CREATE TABLE co_order(
	order_id INT PRIMARY KEY,
	order_oid INT not null,
	order_total DECIMAL(10,2) not null,
	time DATE not null,
	order_table int not null,
	count VARCHAR(32) not null
);
-- -------------
-- 创建口味表
-- -------------
DROP TABLE IF EXISTS co_taste;
CREATE TABLE co_taste(
	id INT PRIMARY KEY,
	taste_name VARCHAR(16),
	taste_image VARCHAR(50) DEFAULT NULL ,
	taste_detail VARCHAR(64) DEFAULT NULL
);
INSERT INTO co_taste (`id`, `taste_name`,`taste_image`,`taste_detail`) VALUES (1,'番茄火锅','2019110413272918818.jpg','精选日照充裕的阳光番茄，制酱炒制而成的番茄底料；推荐搭配河鲜类食材。'),(2,'经典麻辣火锅','2019110413285817992.jpg','选用精选牛油与青花椒和红花椒、及多种辣椒慢火熬煮而成。锅底油色红亮、汤味醇厚，越煮越香不变味。涮食菜品时搭配蒜泥香油味碟，这样吃才地道。'),(3,'菌汤火锅','2019110413280674030.jpg','锅底汤色黄褐，清澈；菌香浓郁、爽口；推荐搭配涮食菌菇类食材。'),(4,'清油麻辣火锅','2019073010381421191.jpg','严选花椒、辣椒、香辛料原料，地道川味火锅；传统工艺发酵豆瓣；汤底色泽鲜红，口感香辣浓郁；推荐搭配涮食脆弹类动物食材。'),(5,'三鲜火锅','2019110413292460456.jpg','选用现代工艺萃取的结合肉类浓缩骨汤为汤底；可根据喜好搭配不同的特色味碟均可，配合芝麻酱更佳；推荐搭配涮食海鲜类及素食类食材。'),(6,'猪肚鸡火锅','2019073018485044923.jpg','精选猪肚、三黄鸡和慢火熬制的风味猪肚汤料包搭配，鲜香浓郁、椒香扑鼻；推荐搭配涮素食食材。'),(7,'猪肚乌鸡火锅','2019072014391185951.jpg','Q弹猪肚，滑脆乌鸡卷，慢火熬制的浓郁猪骨汤底，鲜香够味；推荐搭配弹脆类动物食材及素食食材。'),(8,'椒香火锅','2019071617124420269.png','高温萃取和慢火熬制而成；推荐搭配涮食脆弹类动物食材。'), (9,'辣旋风火锅','2019071617121228900.png','辣味升级，青红鲜椒、花椒油大火翻炒而成；鲜椒自然浓郁、口味鲜辣清爽，推荐搭配涮食脆弹类动物食材。'), (10,'牛肉汤火锅','2019071617113585455.png','通过慢火熬煮，汤味醇正，肉香浓郁，咸鲜而不腻；推荐搭配涮素食食材。'), (11,'青椒火锅','2019071617105771257.png','精选青辣椒、青花椒大火熬制2小时；只用植物油，好吃不油腻，推荐搭配涮食脆弹类动物食材。'), (12,'泰式冬阴功火锅','2019071617101760266.png','冬阴功，“冬阴”是酸辣的意思，“功”是虾的意思，是泰国的一道富有特色的酸辣口味汤品，在泰国非常普遍。推荐搭配海鲜类物食材。'), (13,'鸭掌火锅','2019071617075255688.png','卤制鸭掌配以辣油锅底；汤底色泽鲜红，口感香辣浓郁，推荐搭配涮食脆弹类动物食材。');

-- ------------
-- 创建菜单分类表
-- ------------
DROP TABLE IF EXISTS co_menu_class;
CREATE TABLE co_menu_class(
	id INT(10) PRIMARY KEY,
	name VARCHAR(32)
);
INSERT INTO co_menu_class (id, name) VALUES (1,'new'),(2,'special'),(3,'meat'),(4,'seafood'),(5,'meatball'),(6,'vagetables'),(7,'mushroom'),(8,'dipping'),(9,'snack'),(10,'drink');

-- -------------
-- 创建菜单表
-- -------------
DROP TABLE IF EXISTS co_menu;
CREATE TABLE co_menu(
	m_id INT PRIMARY KEY,
	m_name VARCHAR(32) not null,
	image VARCHAR(128) DEFAULT NULL,
	detail VARCHAR(128),
	price DECIMAL(6,2) not null,
	spec VARCHAR(64) not null,
	class_id INT(10) 
);
INSERT INTO co_menu (m_id,m_name, image,detail,price,spec,class_id) VALUES (1,'火锅牛排','2019080319533244168.jpg','火锅牛排是选用牛肩胛中心部位较嫩的肉－牛板腱，也称牛牡蛎肉，每头牛仅有约3公斤。板腱肉经过解冻、去筋膜、分割、切片、腌制等多道复杂工序，调理后口感嫩有嚼劲，肉香味十足。',60,'份',1),(2,'三豆鲜腐竹','2019072014450019253.jpg','采用精选的非转基因黄豆、黑豆、青豆为原料，经过浸泡清洗、煮浆、揭皮而成的鲜腐竹，通过速冻工艺保证产品新鲜，口感鲜嫩，有嚼劲，豆香味浓郁，且营养丰富，摆盘美观。',56,'份',1),(3,'泰国黑虎虾','2019072014050894920.jpg','优选泰国纯海水养殖的黑虎虾，挑拣其品质大规格符合海底捞统一标准的黑虎活虾，其品种黑虎虾的虾青素含量较普通虾高出很多。而活虾直接蒸煮的工艺可最大程度保留虾的鲜嫩，使上桌时还可尝到黑虎虾刚捕捞时的鲜美滋味。',68,'份',1),(4,'捞派捞面','2019090416445732848.jpg','海底捞明星产品，使用国内优质的小麦高筋面粉，坚持传统酵母发酵工艺。捞面师现场为你舞面，现做现吃，是海底捞自创的体验。',5,'条',2),(5,'捞派豆花','2019090416464340430.jpg','采用非转基因大豆磨浆，经传统手工工艺压制而成。锅开后再煮5分钟左右即可食用。配上豆花蘸碟，口味更突出。',30,'份',2),(6,'捞派麻辣滑牛肉','2019071714291423614.jpg','使用的牛肉是大小米龙和嫩肩肉，是牛的后腿和前腿中最嫩的部位，形状像黄瓜，俗称：黄瓜条。每份滑牛都要经过解冻、去筋膜、分割、切片、腌制等9道复杂工序，口感滑嫩，久煮不老，是海底捞必点菜品。',62,'份',2),(7,'捞派秘制羊肉','2019072014582336764.jpg','选用12个月龄以内的精修羔羊后腿肉，肉质紧实，肉嫩、筋少而几乎无膻味;再选用特色红油腌制入味，肉香与油香充分融合，一口咬下去鲜嫩多汁、肉味十足。',56,'份',2),(8,'捞派毛肚','2019090416505297767.jpg','选自牛的草肚，采用流水清洗、撕片，分切等工艺，加入葱姜、料酒、花椒，冰水浸泡而成。口感脆嫩，肉感十足，锅开后涮10-15秒即可食用。',62,'份',2),(9,'捞派脆毛肚','2019090416474550705.jpg','选自牛的草肚，采用流水清洗、撕片等工艺，加入木瓜蛋白酶精工而成。口感脆嫩，锅开后再采用“七上八下”的方法涮15秒即可食用。',70,'份',2),(10,'捞派鸭肠','2019090416511554708.jpg','选用资质和证件齐全的屠宰场鸭肠，确保每根鸭肠无杂物，口感自然脆爽有嚼劲。',44,'份',2),(11,'捞派黄喉','2019090416503341153.jpg','精选猪黄喉，通过去筋膜、清水浸泡15小时以上，自然去除黄喉的血水和腥味。口感脆嫩弹牙。',62,'份',2),(12,'捞派巴沙鱼片','2019090416493048264.jpg','精选越南湄公河流域养殖的巴沙鱼。经工厂低温车间宰杀、快速去皮等工艺加工成鱼柳，再通过海底捞中央厨房秘制调理而成。口味鲜嫩，且无刺无腥味，特别适合老人、小孩食用。',46,'份',2),(13,'捞派鱼饼','2019072014521672386.jpg','精选阿拉斯加深海野生狭鳕鱼为主要原料，经日式配方和工艺制作而成，鳕鱼含量高，富含深海鳕鱼所特有的营养物质和微量元素。色泽金黄，弹性十足，鲜嫩美味，特别适合老人、小孩、等人群食用。',52,'份',2),(14,'捞派肥牛','2019090416520953297.jpg','肥牛是精选谷饲100天以上的牛经过一定的温度、湿度、风速的环境下使牛的肌肉纤维发生变化排酸处理后，自然块分割，刨成薄片的牛肉，其口感细腻、化渣，肉味十足。',58,'份',3),(15,'精品肥牛','2019090416531168469.jpg','肥牛是精选进口、自然生长谷饲100天以上的牛。每头牛只选用牛背部第1-6根肋骨上脑部位，一头牛产20公斤左右。排酸后自然块分割，刨成薄片的牛肉。口感更细腻、化渣。',68,'份',3),(16,'捞派麻辣滑牛肉','2019090416523393910.jpg','使用的牛肉是大小米龙和嫩肩肉，是牛的后腿和前腿中最嫩的部位，形状像黄瓜，俗称：黄瓜条。每份滑牛都要经过解冻、去筋膜、分割、切片、腌制等9道复杂工序，口感滑嫩，久煮不老，是海底捞必点菜品。',60,'份',3),(17,'火锅牛排','2019072015534548937.jpg','火锅牛排是选用牛肩胛中心部位较嫩的肉～牛板腱，也称牛牡蛎肉，每头牛仅有约3公斤。板腱肉经过解冻、去筋膜、分割、切片、腌制等多道复杂工序，调理后口感嫩有嚼劲，肉香味十足。',68,'份',3),(18,'兆湛撒尿牛肉丸','2019072015522482101.jpg','选用牛后腿部位牛霖，经过排酸、绞碎、搅打成的牛肉滑，捏成丸子后，里面裹入用老鸡、火腿等精心熬制的汤冻。锅开后浮起来再煮3分钟左右即可食用。配上丸滑蘸碟，风味更突出。撒尿牛肉丸中心汤汁温度较高，食用时小心被汤汁烫到。',42,'份',3),(19,'草原羔羊肉','2019090416442437687.jpg','选自内蒙古锡林郭勒大草原（含乌拉盖草原）、呼伦贝尔大草原、科尔沁大草原的12月龄以下羔羊，经过排酸、精选分切、速冻而成。鲜嫩多汁，锅开后涮30秒左右即可食用。',58,'份',3),(20,'新西兰羔羊肉','2019072015510628549.jpg','进口新西兰精选羔羊的前四分体、肩片为原料，在国内经过分割，精修，肥瘦搭配后压制成型;开锅后烫煮1分钟左右即可食用。',78,'份',3),(21,'捞派秘制羊肉','2019072015462773546.jpg','选用12个月龄以内的精修羔羊后腿肉，肉质紧实，肉嫩、筋少而几乎无膻味;再选用特色红油腌制入味，肉香与油香充分融合，一口咬下去鲜嫩多汁、肉味十足。',58,'份',3),(22,'','','','','份',3),(23,'','','','','份',3),(24,'','','','','份',3),(25,'','','','','份',3),(26,'','','','','份',3),(27,'','','','','份',3),(28,'','','','','份',3),(29,'','','','','份',3),(30,'','','','','份',3),(31,'','','','','份',3),(32,'','','','','份',3),(33,'','','','','份',3),(34,'','','','','份',3),(35,'','','','','份',3),(36,'捞派巴沙鱼片','2019090416453138931.jpg','精选越南湄公河流域养殖的巴沙鱼。经工厂低温车间宰杀、快速去皮等工艺加工成鱼柳，再通过海底捞中央厨房秘制调理而成。口味鲜嫩，且无刺无腥味，特别适合老人、小孩食用。',66,'份',4),(37,'罗非鱼','2019072016010522276.jpg','海底捞细选高蛋白、低胆固醇、低脂肪、含有多种不饱和脂肪酸的人工饲养淡水鱼，经过特殊方式调理，使口感松嫩，无骨无刺，余味悠长。',58,'份',4),(38,'鱿鱼须','2019072016003916650.jpg','选择新鲜紫管真鱿、阿鱿品种，采用鱿鱼头部位，根据规格大小进行分切、快速清洗冷冻，保证产品口感原汁原味，涮锅建议1-2分钟使用口感最佳。',58,'份',4),(39,'泰国黑虎虾','2019072014050894920.jpg','优选泰国纯海水养殖的黑虎虾，挑拣其品质大规格符合海底捞统一标准的黑虎活虾，其品种黑虎虾的虾青素含量较普通虾高出很多。而活虾直接蒸煮的工艺可最大程度保留虾的鲜嫩，使上桌时还可尝到黑虎虾刚捕捞时的鲜美滋味。',68,'份',4),(40,'招牌虾滑','2019090416552617875.jpg','精选南美品种白虾虾仁，通过海底捞独有的虾滑生产工艺，虾肉含量高达93%，上桌前至少手工摔打10次，Q弹十足，入口爽滑鲜甜招牌虾滑是海底捞的经典食材之一。建议涮锅3分钟味道最佳。',60,'份',5),(41,'鳕鱼虾滑','2019072016074346937.jpg','选择富含多种维生素深海鳕鱼，搭配海底捞独特虾滑，健康营养，涮锅3分钟，左右即可食用。',68,'份',5),(42,'蟹味棒','2019072016071863443.jpg','精选阿拉斯加深海野生狭鳕鱼和金线鱼为主要原料，经日式配方和工艺制作而成，深海鱼糜含量高，富含深海鱼类所特有的营养物质和微量元素。产品丝丝细嫩，弹性十足，鲜甜美味，特别适合老人、小孩、等人群食用。',48,'份',5),(43,'鱼豆腐','2019072016064250547.jpg','选用优质鱼肉，配以佐料，搅打、蒸制而成。细嫩鲜滑，鱼香味浓。锅开后再煮2分钟左右即可食用。',39,'份',5),(44,'捞派鱼饼','2019072014521672386.jpg','精选阿拉斯加深海野生狭鳕鱼为主要原料，经日式配方和工艺制作而成，鳕鱼含量高，富含深海鳕鱼所特有的营养物质和微量元素。色泽金黄，弹性十足，鲜嫩美味，特别适合老人、小孩、等人群食用。',52,'份',5),(45,'娃娃菜','2019072117095752153.jpg','火锅常备菜。来自山东、云南、河北等地的精选新鲜娃娃菜，经挑选、清洗、切配而成，口感脆嫩，涮煮2分钟左右。',26,'份',6),(46,'蒿子秆','2019072117103212628.jpg','火锅常备菜。来自云南、山东、河北、江苏等地的优质新鲜蒿子杆，风味独特，经挑选、清洗、切配而成，涮煮1分钟左右即可食用。',26,'份',6),(47,'菠菜','2019072117104169232.jpg','精选云南，河北、江苏等地的新鲜菠菜，绿色健康，经过挑选、清洗、切配而成，涮煮1分钟左右即可食用。',26,'份',6),(48,'长生菜','2019072117105237280.jpg','来自山东、云南、河北、江苏等地的新鲜长生菜、经过挑选、清洗、切配而成，口感脆嫩，涮1分钟左右即可食用。',26,'份',6),(49,'大白菜','2019072117110572252.jpg','精选新鲜大白菜，经过挑选、清洗、切配而成，口感脆嫩，绿色健康，涮煮2分钟左右即可食用。',26,'份',6),(50,'香菜','2019072117112233677.jpg','优选自山东、云南、河北、江苏等产地新鲜香菜，风味独特，涮煮2-3分钟左右即可食用。',26,'份',6),(51,'','','','','份',6),(52,'','','','','份',6),(53,'','','','','份',6),(54,'','','','','份',6),(55,'','','','','份',6),(56,'','','','','份',6),(57,'','','','','份',6),(58,'','','','','份',6),(59,'','','','','份',6),(60,'','','','','份',6),(61,'','','','','份',6),(62,'金针菇','2019072016023663827.jpg','火锅常备菜，来自上海、武汉、山东等地的新鲜金针菇，工业化种植保证产品质量，经过挑选、清洗、装盘而成。菌盖滑嫩、清脆，锅开后再煮2分钟左右即可食用。',28,'份',7),(63,'香菇','2019072016014133462.jpg','来自河北、东北等地的新鲜香菇，工厂化种植，保证产品质量安全，经过挑选、清洗、切配、装盘而成。香嫩爽滑，锅开后再煮4分钟左右即可食用。',28,'份',7),(64,'舞茸菌','2019072016002753412.jpg','来自湖南、上海等地的新鲜舞茸菌，经过挑选、清洗、装盘而成。清香四溢，似花非花、入汤奇香，涮煮3分钟。',36,'份',7),(65,'木耳','2019072015583036366.jpg','经过浸泡、挑选、装盘而成。口感脆嫩，久煮不化，营养健康，锅开后再煮3分钟左右即可食用，口感爽脆。',28,'份',7),(66,'蒜泥香油碟','2019071715521664521.png','川式口味',10,'份',8),(67,'鲜香味碟','2019071714545049670.png','提鲜必备',10,'份',8),(68,'香辣味碟','2019071715482473673.png','川渝搭档',10,'份',8),(69,'海鲜味碟','2019080206451191855.png','海鲜专属',10,'份',8),(70,'丸滑味碟','2019071715521664521.png','丸滑必备',10,'份',8),(71,'酸辣味碟','2019071715492949710.png','酸辣开胃',10,'份',8),(72,'现炸酥肉','2019071714582120048.jpg','选用上等五花肉切条，蛋液、红薯粉上浆，色泽金黄、肥而不腻、配上干辣椒碟或者番茄酱食用，口感香脆，风味独特，直接涮火锅食用也超赞哦。',46,'份',9),(73,'茴香小油条','2019071714575545818.jpg','配以鲜茴香和茴香籽，嚼起来松软，满口茴香味回味悠长，趁热吃外皮酥脆口感最佳。',14,'份',9),(74,'葱味飞饼','2019071714572743206.jpg','外表呈金黄色，饼丝一层层缠绕，配以小香葱，入口酥脆葱香味十足。',24,'份',9),(75,'金银馒头','2019071714565997587.jpg','外表一半洁白，一半金黄，加入奶粉馒头散发出浓郁的奶香，入口银馒头松软香甜，金馒头酥脆焦香。',24,'份',9),(76,'蛋炒饭','2019071714563731940.jpg','选用东北黑龙江五常地区寒地黑土种植的五优四号稻谷，是可追溯、可溯源的大米。经过精细加工、淘洗、蒸煮后加鸡蛋、食用盐、葱花精工炒制而成，口感糯香。',10,'碗',9),(77,'五常米饭','2019071714561092925.jpg','选用可追溯、可溯源的大米，经过精细加工、淘洗、蒸煮而成，米粒饱满、饭粒油亮、香味浓郁，清淡略甜、芳香爽口。',5,'碗',9),(78,'海底捞经典大麦啤酒','2019071715065733526.jpg','海底捞邀请台湾金色三麦研发和监制，由德国优布劳公司在中国的工厂酿造，是我公司第一款自有品牌的啤酒，原料选用澳大利亚和德国的大麦芽、德国啤酒花,产品有明显的大麦芽和啤酒花香气。',15,'瓶',10),(79,'海底捞德式小麦啤酒','2019071715062737965.jpg','海底捞邀请台湾金色三麦研发和监制，由德国优布劳公司在中国的工厂酿造。该啤酒采用德国南部巴伐利亚地区传统小麦啤酿造工艺，泡沫绵密细腻，拥有水果、麦芽、酒花的香气相融的复合香气，口感更加温和细腻顺滑。适合口味清淡的人和女士饮用。',15,'瓶',10),(80,'海底捞乳酸菌饮料（草莓味）','2019071715055495593.jpg','海底捞联合中国农业大学研发的一款搭配火锅喝的饮料，能够有效缓解辣感，调节肠道菌群，从工厂到餐桌2-8℃全程冷链保鲜。该饮品是零脂肪的活性乳酸菌，每瓶活菌数能够达到235亿（每毫升1亿活菌数），能够起到保护肠胃，促进消化的作用。',10,'瓶',10),(81,'海底捞乳酸菌饮料（原味）','2019080221024283286.jpg','海底捞联合中国农业大学研发的一款搭配火锅喝的饮料，能够有效缓解辣感，调节肠道菌群，从工厂到餐桌2-8℃全程冷链保鲜。该饮品是活性乳酸菌，能够起到保护肠胃，促进消化的作用。',10,'瓶',10);
-- ---------
-- 创建评价表
-- --------
DROP TABLE IF EXISTS co_evaluate;
CREATE TABLE co_evaluate(
	e_id INT PRIMARY KEY,
	e_username VARCHAR(32),
	category_id SMALLINT(5) unsigned NOT NULL COMMENT '外键,菜单ID',
  author_id INT(10) unsigned NOT NULL COMMENT '外键,用户ID',
	create_at INT(10) unsigned NOT NULL COMMENT '发表日期',
	description VARCHAR(255) NOT NULL COMMENT '评价内容',
	image VARCHAR(50) DEFAULT NULL COMMENT '评价图'
);




